const MIN_SIZE = 20;
const DEFAULT_SIZE = 30;

export default function Logo({ color, size, className }: LogoProps) {
  const getColorValue = (colorInput?: string) => {
    if (!colorInput) {
      return '#ffffff';
    }

    const tailwindColors = {
      white: '#ffffff',
      black: '#000000',
      transparent: 'transparent',
      current: 'currentColor',
      red: '#ef4444',
      orange: '#f97316',
      amber: '#f59e0b',
      yellow: '#eab308',
      lime: '#84cc16',
      green: '#22c55e',
      emerald: '#10b981',
      teal: '#14b8a6',
      cyan: '#06b6d4',
      sky: '#0ea5e9',
      blue: '#3b82f6',
      indigo: '#6366f1',
      violet: '#8b5cf6',
      purple: '#a855f7',
      fuchsia: '#d946ef',
      pink: '#ec4899',
      rose: '#f43f5e',
      gray: '#6b7280',
      slate: '#64748b',
      zinc: '#71717a',
      neutral: '#737373',
      stone: '#78716c',

      // Your custom crown colors from tailwind.config.js
      'crown-primary': '#AE3537',
      'crown-complementary': '#FFDAC9',
      'crown-dark': '#1F1F1F',
      'crown-white': '#FDF4F4',
      'crown-dark-red': '#661E1F',
    };

    const tailwindColor = tailwindColors[colorInput as keyof typeof tailwindColors];

    if (tailwindColor) {
      return tailwindColor;
    }

    return colorInput;
  };

  const logoColor = getColorValue(color);

  const containerClass = 'inline-flex items-center';

  const logoStyle = {
    width: Math.max(size || DEFAULT_SIZE, MIN_SIZE),
    height: Math.max(size || DEFAULT_SIZE, MIN_SIZE),
  };

  const svgStyle = {
    width: '100%',
    height: '100%',
    minWidth: 'auto',
    minHeight: 'auto',
  };

  return (
    <div className={`${containerClass} ${className}`} style={logoStyle}>
      <div style={svgStyle}>
        <CrownSVG color={logoColor} />
      </div>
    </div>
  );
}
